def grid():    for row in board:        print(" | ".join(row))    return boarddef score(board,row,col,player):    if row in range(3) and col in range(3):        if board[row][col] == " ":            board[row][col] = player            check(board,player)            grid()        else:            print("This Spot Is Occupied, Try Again: ")            return False    else:        print("Index Out of Range, Try Again: ")        return Falsedef check(board,player):    #checks horizontally and verticaly    for row in range(len(board)):        count = 0        for col in range(len(board[row])):            if board[row][col] == player:                count += 1            else:                count = 0        if count == 3:            print("You Win")            return True    for row in range(len(board)):        count = 0        for col in range(len(board[col])):            if board[col][row] == player:                count += 1            else:                count = 0        if count == 3:            print("You Win")            return True    #diagnol top-left to bottom-right    count = 0    for row in range(len(board)):        if board[row][row] == player:            count += 1        else:            count = 0    if count == 3:        print("You Win")        return True    #diagnol top-right to bottom-left    count = 0    for row in range(len(board)):        if board[row][len(board)-1-row] == player:            count += 1        else:            count = 0    if count == 3:        print("You Win")        return Truedef draw(board):    check = " "    for row in board:        if check not in row:            print("Its a Draw")            return True    else:        return Falseif __name__ == '__main__':    board = [[" " for x in range(3)] for x in range(3)]    icon = ["X","O"]    user = 0    legal = False    while not legal:        if user == 0:            row = int(input("Row: "))            col = int(input("Col: "))            player = icon[0]            score(board,row,col,player)            user += 1        if user == 1:            row = int(input("Row: "))            col = int(input("Col: "))            player = icon[1]            score(board,row,col,player)            user -= 1